import '../css/Home.css';
import '../css/Form.css'
import { useState } from 'react';
import axios from 'axios';
axios.defaults.withCredentials = true;

const Home = (props) => {
    const nome = props.nome;

    const [foto, setFoto] = useState();
    const handleLoginForm = async (e) => {
        const url = "http://localhost:8000/updateImage/";
        const dati = new FormData();
        dati.append("foto", foto, foto.name);

        //alert(dati)

        fetch (url, {
            method: "POST",
            body: dati
        })
        .then(res => alert(res))
        .catch(err => alert(err));


        /*
        const corpo = {
            "foto": foto,
            withCredentials: true
        }

        const risposta = await axios.post(url, corpo);
        const oggetto = risposta.data;

        alert(oggetto.isTuttoOk)
        */

    }

    return (
        <div className="divHome">
            <h1>Benvenuto</h1>
            <h2>{nome}</h2>

            <form onSubmit = {handleLoginForm}>
                <input type="file" onChange={(e) => setFoto(e.target.files[0])}/>
                <button id='bottone' type='submit'> Invia</button>
            </form>
        </div> 



    );
}

export default Home;
