import '../css/Home.css';
import axios from 'axios';
import { useState } from 'react';
axios.defaults.withCredentials = true;

const Home = (props) => {
    const nome = props.nome;

    var file = null;

    const fileSelector = event => {

        file = event.target.files[0];
    }
    const handleLoginForm = async (e) => {

        const url = "http://localhost:8000/uploadImage/";

        const invio = new FormData();
        alert("gigi")



        try {
            invio.append("foto", file, file.name);
            const risposta = await axios (url, file);
            //const oggetto = risposta.data;
            //alert(oggetto["messaggio"]);
        }
        catch (error) {
            alert("ERRORE: "+error);
        }


    }

    return (
        <div className="divHome">
            <h1>Benvenuto</h1>
            <h2>{nome}</h2>

            <form onSubmit = {handleLoginForm}>
                <input type="file" onChange={fileSelector}/>
                <button id='bottone' type='submit'> Invia</button>
            </form>


        </div> 



    );
}

export default Home;
