import '../css/Home.css';
import axios from 'axios';
import { useState } from 'react';
axios.defaults.withCredentials = true;

const Home = (props) => {
    const nome = props.nome;

    const [state, setState] = useState(null)

    const fileSelector = event => {
        //alert(event.target.files[0])
        setState(event.target.files[0]);
        alert("gianni")
    }
    const handleLoginForm = async (e) => {
        const url = "http://localhost:8000/uploadImage/";

        const file = new FormData();
        alert("gigi")



        try {
            file.append("foto", e.target.files[0], e.target.files[0].name);
            const risposta = await axios (url, file);
            const oggetto = risposta.data;
            alert(oggetto["messaggio"]);
        }
        catch (error) {
            alert(error);
          } 
    }

    return (
        <div className="divHome">
            <h1>Benvenuto</h1>
            <h2>{nome}</h2>

            <form onSubmit = {handleLoginForm}>
                <input type="file" onChange={fileSelector}/>
                <button id='bottone' type='submit'> Invia</button>
            </form>


        </div> 



    );
}

export default Home;
